1,"object":"page","menu_item_parent":0,"position":4,"type":"post_type","title":"Houses","url":"http:\\/\\/localhost\\/u3100579_project2\\/?page_id=21","target":"","attr_title":"","description":"","classes":"","xfn":"","status":"publish","original_title":"Houses","nav_menu_term_id":-2239563316958029800,"_invalid":false,"type_label":"Page"},"type":"nav_menu_item","user_id":2,"date_modified_gmt":"2019-04-23 05:09:47"},"nav_menu_item[-7188952935575081000]":{"value":{"object_id":22,"object":"page","menu_item_parent":0,"position":5,"type":"post_type","title":"Architects","url":"http:\\/\\/localhost\\/u3100579_project2\\/?page_id=22","target":"","attr_title":"","description":"","classes":"","xfn":"","status":"publish","original_title":"Architects","nav_menu_term_id":-2239563316958029800,"_invalid":false,"type_label":"Page"},"type":"nav_menu_item","user_id":2,"date_modified_gmt":"2019-04-23 05:09:47"},"nav_menu_item[-3440754026895220700]":{"value":{"object_id":23,"object":"page","menu_item_parent":0,"position":6,"type":"post_type","title":"About Us","url":"http:\\/\\/localhost\\/u3100579_project2\\/?page_id=23","target":"","attr_title":"","description":"","classes":"","xfn":"","status":"publish","original_title":"About Us","nav_menu_term_id":-2239563316958029800,"_invalid":false,"type_label":"Page"},"type":"nav_menu_item","user_id":2,"date_modified_gmt":"2019-04-23 05:10:47"},"nav_menu_item[-6234394101761878000]":{"value":{"object_id":24,"object":"page","menu_item_parent":0,"position":7,"type":"post_type","title":"House Locations","url":"http:\\/\\/localhost\\/u3100579_project2\\/?page_id=24","target":"","attr_title":"","description":"","classes":"","xfn":"","status":"publish","original_title":"House Locations","nav_menu_term_id":-2239563316958029800,"_invalid":false,"type_label":"Page"},"type":"nav_menu_item","user_id":2,"date_modified_gmt":"2019-04-23 05:11:05"}}', '', '', 'trash', 'closed', 'closed', '', '5a8f24d0-efb2-4326-b53b-fd88a2b3581e', '', '', '2019-04-23 05:11:0